sql.where:
ph.UH_U_ID = #{uId}
AND po_orderdate >= SYSDATE - INTERVAL '7' DAY
AND po_orderdate >= SYSDATE - INTERVAL '15' DAY
AND po_orderdate >= SYSDATE - INTERVAL '1' MONTH
AND TO_CHAR(po_orderdate, 'YYYY-MM') = #{pageRequestDTO.recentMonths}
AND po_orderdate BETWEEN TO_DATE(#{pageRequestDTO.startDate}, 'YYYY-MM-DD') AND TO_DATE(#{pageRequestDTO.endDate}, 'YYYY-MM-DD')
