sql.where:
PR_U_ID = #{uId}
AND PR_REG_DATE >= SYSDATE - INTERVAL '7' DAY
AND PR_REG_DATE >= SYSDATE - INTERVAL '15' DAY
AND PR_REG_DATE >= SYSDATE - INTERVAL '1' MONTH
AND TO_CHAR(PR_REG_DATE, 'YYYY-MM') = #{pageRequestDTO.recentMonths}
AND PR_REG_DATE BETWEEN TO_DATE(#{pageRequestDTO.startDate}, 'YYYY-MM-DD') AND TO_DATE(#{pageRequestDTO.endDate}, 'YYYY-MM-DD')
